function statistics(verbose, messageFactory) {
    let count = 0, names = verbose ? null : [];
    return (name) => {
        if (name != null) {
            count++;
            if (names) {
                if (count > 5) {
                    names = null;
                }
                else {
                    names.push(name);
                }
            }
            return;
        }
        return messageFactory(!names ? count : names);
    };
}

export { statistics as default };
